import { createContext, useState } from "react";

export const ContextAUTH = createContext()

const ContextAUTHProvider = ({children}) => {
    //const [localStorageValue, setLocalStorageValue] = useState(localStorage.getItem('monToken'))
    const [token, setToken] = useState(null)
    const [login, setLogin] = useState(null)
    const [id, setID] = useState(null)
    const [isLogged, setIsLogged] = useState(false)

    const [data, setData] = useState({
        id,
        isLogged,
        token,
        login
    });

    const loginHandler = (token, id, login) => {
        setData({
            ...data,
            id,
            isLogged: true,
            token,
            login
        });

        const myStorageToken = localStorage.setItem('monToken', token)
        const myStorageID = localStorage.setItem('monID', id)
        const myStorageLogin = localStorage.setItem('monLogin', login)
    }

    const logoutHandler = () => {
        setData({
            ...data,
            id:null,
            isLogged: false,
            token:null,
            login:null
        });

        setLocalStorageValue('')
        localStorage.removeItem('monToken')
    }

    return (
        <ContextAUTH.Provider value={{data, loginHandler, logoutHandler, localStorageValue}}>
            {children}
        </ContextAUTH.Provider>
    )
}

export default ContextAUTHProvider;
