import { createContext, useState } from "react";

export const ContextAUTH = createContext()

const ContextAUTHProvider = ({children}) => {
    const [localStorageValue, setLocalStorageValue] = useState(null)
    const [token, setToken] = useState(null)
    const [userID, setUserID] = useState(null)
    const [isLogged, setIsLogged] = useState(false)

    const [data, setData] = useState({
        userID,
        isLogged,
        token,
    });

    console.log(localStorageValue);

    const loginHandler = () => {
        setData({
            ...data,
            userID:"id",
            isLogged: true,
            token: "123456789",
        });

    const myStorage = localStorage.setItem('monToken', data.token)
    setLocalStorageValue(myStorage)
    }
    console.log(data);

    const logoutHandler = () => {
        setData({
            ...data,
            userID:null,
            isLogged: false,
            token:null,
        });
    }

    return (
        <ContextAUTH.Provider value={{data, loginHandler, logoutHandler}}>
            {children}
        </ContextAUTH.Provider>
    )
}

export default ContextAUTHProvider;
