import React, { useEffect, useState } from 'react'
import { useContext } from 'react'
import { ContextAUTH } from '../context/ContextAuth'
import ButtonSubmit from './UI/ButtonSubmit'
import FicheTest from './FicheTest'

const Test = (props) => {
  const dataContext = useContext(ContextAUTH);
  const [dataFicheUser, setDataFicheUser] = useState([])

  useEffect(()=>{
    fetch(`http://localhost:3000/crud/readOne/${dataContext.data.id}`, {
        headers: {
            "Accept" : 'application/json',
            "Authorization" : `Bearer ${dataContext.data.token}`
        }
    })
    .then(response => response.json())
    .then(data => setDataFicheUser(data.message))
    .catch( error => console.error(error))
  },[]);

  const showDetailsFicheUser = async(id) => {;
    const ficheUserAPI = await fetch(`http://localhost:3000/crud/readOneFicheUser/${id}`)
    const responseFicheUser = await ficheUserAPI.json()
  }

  return (
    <div style={{color:"#f1f1f1", textAlign:"center", marginTop:"40px"}}>
        {
            <>
                <h3>id: {dataContext.data.id}</h3>
                <h1>Bienvenue, {dataContext?.data?.loginConnect?.split('@')[0]}</h1>
                <hr />
                <div>
                    <h1>Fiche user</h1>
                    {
                        dataFicheUser.map(user => {
                            console.log(user)
                            return (
                                <FicheTest 
                                    data={user} 
                                    clickDetails={()=>showDetailsFicheUser()} 
                                />
                            )
                        })
                    }
                </div>
                <ButtonSubmit 
                    click={props.logout} 
                    color={"danger"} 
                    type={"button"}
                >
                    Se deconnecter
                </ButtonSubmit>
                <hr />
            </> 
        }
    </div>
  )
}

export default Test
