    const [userID, setUserID] = useState(null);

    const loginHandler = (token, userID) => {
        setData({
            ...data,
            isLogged: true
        });
        setToken(token);
        setUserID(userID);
    }

    const logoutHandler = () => {
        setData({
            ...data,
            isLogged: false,
            token
        });
        setToken(null);
        setUserID(null);
    }

    return (
        <ContextAUTH.Provider value={{data}}>
            {children}
        </ContextAUTH.Provider>
    )
}

export default ContextAUTHProvider;
