import { createContext, useState } from "react";

export const ContextAUTH = createContext()

const ContextAUTHProvider = ({children}) => {

    const tokenLocalStorage = localStorage.getItem('monToken')
    const userIDLocalStorage = localStorage.getItem('userID')
    const isLoggedLocalStorage = localStorage.getItem('isLogged')

    const [token, setToken] = useState(tokenLocalStorage)
    const [userID, setUserID] = useState(userIDLocalStorage)
    const [isLogged, setIsLogged] = useState(false)

    const [data, setData] = useState({
        userID,
        isLogged:false,
        token
    });

    const loginHandler = (token, userID) => {
        setToken(token)
        setUserID(userID)

        setData({
            ...data,
            userID,
            isLogged: true,
            token
        });
        console.log(data.isLogged)
        localStorage.setItem('monToken', token);
        localStorage.setItem('isLogged', data.isLogged);
        localStorage.setItem('userID', userID);
    }

    const logoutHandler = () => {
        setData({
            ...data,
            userID:setUserID(null),
            isLogged: false,
            token:setToken(null)
        });
        localStorage.removeItem('monToken');
        localStorage.removeItem('isLogged');
        localStorage.removeItem('userID');
    }

    return (
        <ContextAUTH.Provider value={{data, loginHandler, logoutHandler}}>
            {children}
        </ContextAUTH.Provider>
    )
}

export default ContextAUTHProvider;
