import React from 'react'
import { useContext } from 'react'
import { ContextAUTH } from '../context/ContextAuth'
import ButtonSubmit from './UI/ButtonSubmit'

const Test = (props) => {
  const dataContext = useContext(ContextAUTH);
  console.log(dataContext.data.userID);

  const url = `http://localhost:3000/crud/readOne/${dataContext.data.userID}`

  const fetchHandler = async() => {
        let callAPIUserFiche = await fetch(url, {
            method:"GET",
            body: JSON.stringify({
                userID: dataContext.data.userID
            }),
            headers:{
                "Content-Type":"application/json",
                Authorization:`Bearer Token ${dataContext.data.token}`
            }
        });
        let userFicheResponse = await callAPIUserFiche.json();
        console.log(userFicheResponse)
  }

  fetchHandler()

  return (
    <div style={{color:"#f1f1f1", textAlign:"center", marginTop:"40px"}}>
        {
            <>
                <h3>id: {props.dataUser.userID}</h3>
                <h1>Bienvenue, {props.dataUser.login.split('@')[0]}</h1> 
                <hr />
                <div>
                    <h1>Fiche user</h1>
                    <p>test</p>
                </div>
                <ButtonSubmit 
                    click={props.click} 
                    color={"danger"} 
                    type={"button"}
                >
                    Se deconnecter
                </ButtonSubmit>
                <hr />
            </> 
        }
    </div>
  )
}

export default Test
