import React, { useContext, useEffect, useRef, useState } from 'react'
import { ContextAUTH } from '../context/ContextAuth';
import ButtonSubmit from './UI/ButtonSubmit';

const FicheTest = ({data}) => {
  /*
    On créer ici d'autres state afin de pouvoir mettre à jour les données
    si on souhaite les modifier
  */
  console.log(data)
  console.log(Object.values(data === "undefined" && ""))

  const [dataUpdate, setDataUpdate] = useState(data);
  const [valueInput, setValueInput] = useState(data)
  const [isUpdate, setIsUpdate] = useState(false);

  const dataContext = useContext(ContextAUTH);
  // console.log(dataContext.data.token)

  const nameInput = useRef();
  const imageInput = useRef();

  const [detailsFicheUser, setDetailsFicheUser] = useState([]);

  const modifiyData = () => {
    setIsUpdate(curr => !curr);
    console.log(dataUpdate)
    console.log(JSON.stringify(dataUpdate))

    const url = `http://localhost:3000/crud/update/${dataUpdate.id}`;
    fetch(url, {
      method:"PUT",
      headers:{
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        "Authorization" : `Bearer ${dataContext.data.token}`
      },
      body:JSON.stringify(dataUpdate)
    })
    .then(res => res.json())
    .then(data => console.log(data))
  }


  const deleteData = () => {
    const url = `http://localhost:3000/crud/delete/${dataUpdate.id}`;
    fetch(url, {
      method:"DELETE",
      headers:{
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        "Authorization" : `Bearer ${dataContext.data.token}`
      },
      body:JSON.stringify(dataUpdate)
    })
    .then(res => res.json())
    .then(data => console.log(data))
  }

  
  const handleInput = async() => {
    const newName = nameInput?.current?.value;
    const newImage = imageInput?.current?.files[0]?.name;
    setValueInput(newName)
    setDataUpdate(
      {...dataUpdate, 
        name:newName,
        image:newImage
      }
    );       
  }
  
  return (
    <section>
      {
        Object.values(data === "undefined" && "").length < 0 ?
      <>
      <div>
        <hr />

        <div style={{
          margin:"0px 20px",
          display:"flex", 
          justifyContent:"center", 
          gap:"40px"
        }}>
          {
            isUpdate ?
            <div style={{display:"flex", flexDirection:"column"}}>
              <img 
                src={dataUpdate.image} 
                alt="" 
                style={{width:"200px", borderRadius:"20px"}} 
              />
              <input 
                type="file"
                ref={imageInput} 
                onChange={handleInput} 
                accept='.jpeg,.jpg,.png' 
              />
            </div>
            :
            <img 
              src={dataUpdate.image} 
              alt="" 
              style={{width:"200px", borderRadius:"20px"}} 
            />
          }


          <div style={{
            display:"flex", 
            flexDirection:"column", 
            alignItems:"center"
          }}>
            {
              isUpdate ? 
              <>
                <label htmlFor="nom">Nom</label>
                <input 
                  ref={nameInput} 
                  onChange={handleInput} 
                  type="text" 
                  value={dataUpdate.name} 
                  style={{color:"black"}} 
                />
              </>
              :
              <>
                <h4>id : {dataUpdate.id}</h4>
                <h4>Nom : {dataUpdate.name}</h4>
              </>
            }
            <h5>Compte créé le : {dataUpdate.created_at}</h5>
            <br />
          </div>


        </div>
      </div>
      <div style={{display:"flex", gap:"20px", justifyContent:"center", marginTop:"20px"}}>
        <ButtonSubmit click={modifiyData} color={"warning"}>
          {!isUpdate ? "Modifier la fiche" : "Enregister les modifications"}
        </ButtonSubmit>
        <ButtonSubmit click={deleteData} color={"danger"}>Supprimer la fiche</ButtonSubmit>
      </div>

      </>
      :
      <h2 className='text-danger'>Pas de fiche user</h2>
      }

    </section>
  )
}

export default FicheTest
