import React, { useContext, useRef, useState } from 'react'
import ButtonSubmit from "../UI/ButtonSubmit";
import Form from 'react-bootstrap/Form';
import ErrorModalOverlay from './ErrorModalOverlay';
import Loader from '../UI/Loader';
import { ContextAUTH } from '../../context/ContextAuth';

const FormAuth = () => {
  const [show, setShow] = useState(false);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false)
  const [isLogin, setIsLogin] = useState(true);

  const emailInput = useRef(null);
  const passwordInput = useRef(null);

  const loginContext = useContext(ContextAUTH);
  console.log(loginContext)

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log("je login")
    const emailValue = emailInput.current.value;
    const passwordValue = passwordInput.current.value;

    if (emailValue.length === 0) {
      setError('L\'email est pas remplie')
      setShow(true)
    } else if (passwordValue.length === 0) {
      setError('Le password est pas remplie')
      setShow(true)
    } else {


      const urlToLogin = `http://localhost:3000/${isLogin ? "login" : "signUp"}`
      console.log(urlToLogin)
      const checkToLoginOrSignUp = async () => {
        try {
          const responseLogin = await fetch(urlToLogin, {
            method: 'POST',
            body: JSON.stringify({
              login: emailValue,
              password: passwordValue
            }),
            headers: { 'Content-Type': 'application/json' },
            redirect: "follow",
            referrerPolicy: "no-referrer",
            credentials: "same-origin",
          });
          const dataReponseJSON = await responseLogin.json();
          setIsLoading(true)

          //Pour gerer les erreurs il faut voir au niveau de la propriété ok
          if (responseLogin.ok) {
            //Serveur a repondu donc le chargement est terminé
            let loggedIn = {...props.dataLoginContext, isLogged: !props.dataLoginContext.isLogged}
            console.log(loggedIn)
          } else {
            setShow(true)
            setIsLoading(false)
            setError(dataReponseJSON?.message)
            throw Error(dataReponseJSON?.message)
          }

        } catch (err) {
          console.log(err.message);
        }
      }
      checkToLoginOrSignUp();
    }
  }

  const toggleAuthHandler = () => {
    setIsLogin(!isLogin)
    setIsLoading(false)
  }


  return (
    <>
      {
        show && <ErrorModalOverlay value={error} close={() => setShow(false)} />
      }
      {
        // !isLogged ?
          <div className="d-flex flex-column align-items-center justify-content-center border w-50 m-auto p-3 mt-5 rounded" style={{ boxShadow: "5px 5px 10px black", background: "firebrick" }}>
            {
              isLogin ?
                <h1 className="text-light">Se connecter</h1>
                :
                <h1 className="text-light">Créer un compte</h1>
            }

            <Form className="w-75" onSubmit={(e) => handleSubmit(e)}>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label className="text-light">Email address</Form.Label>
                <Form.Control ref={emailInput} type="email" placeholder="Enter email" />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label className="text-light">Password</Form.Label>
                <Form.Control ref={passwordInput} type="password" placeholder="Password" />
              </Form.Group>

              {
                isLoading ?
                  <Loader />
                  :
                  isLogin ?
                    <ButtonSubmit color={"success"}>
                      Se connecter
                    </ButtonSubmit>
                    :
                    <ButtonSubmit color={"primary"}>
                      Créer mon compte
                    </ButtonSubmit>
              }
              {
                !isLogin
                  ?
                  <h4
                    onClick={toggleAuthHandler}
                    style={{ textAlign: "center", margin: "30px 0px 0px", cursor: "pointer" }}
                  >Se connecter</h4>
                  :
                  <h4
                    onClick={toggleAuthHandler}
                    style={{ textAlign: "center", margin: "30px 0px 0px", cursor: "pointer" }}
                  >Créer un compte</h4>
              }
            </Form>
          </div>
          // :
          // <Test clickLogout={()=>props.logoutLoginContext()} />
      }
    </>
  );
}

export default FormAuth
