import React, { useContext, useEffect, useRef, useState } from 'react'
import { ContextAUTH } from '../context/ContextAuth';
import ButtonSubmit from './UI/ButtonSubmit';

const FicheTest = ({data}) => {
  /*
    On créer ici d'autres state afin de pouvoir mettre à jour les données
    si on souhaite les modifier
  */
  const [dataUpdate, setDataUpdate] = useState(data);
  const [valueInput, setValueInput] = useState(data)
  const [isUpdate, setIsUpdate] = useState(false);

  const dataContext = useContext(ContextAUTH);

  const nameInput = useRef();
  const imageInput = useRef();

  const [detailsFicheUser, setDetailsFicheUser] = useState([]);

  const showDetailsFicheUser = async(id) => {
    const ficheUserAPI = await fetch(`http://localhost:3000/crud/readOneFicheUser/${data.id_fiche_user}`,{
      method:"GET",
      headers: {
        "Accept" : 'application/json',
        "Authorization" : `Bearer ${dataContext.data.token}`
    }
    })
    const responseFicheUser = await ficheUserAPI.json()
    console.log(responseFicheUser)
  }

  const modifiyData = () => {
    setIsUpdate(curr => !curr)
  }
  
  const handleInput = () => {
    const newName = nameInput?.current?.value;
    const newImage = imageInput.current;
    setValueInput(newName)
    setDataUpdate(curr => ({...curr, name:newName}) );
    
    console.log(newName)
    console.log(newImage)
  }

  return (
    <section>
      <div onClick={()=>showDetailsFicheUser()}>
        <hr />

        <div style={{display:"flex", justifyContent:"center", gap:"40px"}}>
          {
            isUpdate ?
            <div style={{display:"flex", flexDirection:"column"}}>
              <img src={data.image} alt="" style={{width:"200px", borderRadius:"20px"}} />
              <input ref={imageInput} onInput={()=>handleInput()} type="file" />
            </div>
            :
            <img src={data.image} alt="" style={{width:"200px", borderRadius:"20px"}} />
          }


          <div style={{display:"flex", flexDirection:"column", alignItems:"center"}}>
            {
              isUpdate ? 
              <>
                <label htmlFor="nom">Nom</label>
                <input ref={nameInput} onChange={handleInput} type="text" value={dataUpdate.name} style={{color:"black"}} />
              </>
              :
              <h4>Nom : {dataUpdate.name}</h4>
            }
            <h5>Compte créé le : {dataUpdate.created_at}</h5>
            <br />
          </div>


        </div>
      </div>
      <div style={{display:"flex", gap:"20px", justifyContent:"center"}}>
        <ButtonSubmit click={modifiyData} color={"warning"}>
          {!isUpdate ? "Modifier la fiche" : "Enregister les modifications"}
        </ButtonSubmit>
        <ButtonSubmit color={"danger"}>Supprimer la fiche</ButtonSubmit>
      </div>
    </section>
  )
}

export default FicheTest
