import React, { useContext, useEffect, useRef, useState } from 'react'
import { ContextAUTH } from '../context/ContextAuth';
import ButtonSubmit from './UI/ButtonSubmit';

const FicheTest = ({data}) => {
  /*
    On créer ici d'autres state afin de pouvoir mettre à jour les données
    si on souhaite les modifier
  */

  const [dataUpdate, setDataUpdate] = useState(data);
  const [valueInput, setValueInput] = useState(data)
  const [isUpdate, setIsUpdate] = useState(false);

  const dataContext = useContext(ContextAUTH);
  // console.log(dataContext.data.token)

  const nameInput = useRef();
  const imageInput = useRef();

  const [detailsFicheUser, setDetailsFicheUser] = useState([]);

  const modifiyData = async() => {
    setIsUpdate(curr => !curr);
  }




  const fetchHandlerInput = async() => {

  }
  
  
  
  const handleInput = async() => {
    const newName = nameInput?.current?.value;
    const newImage = imageInput?.current?.files[0]?.name;
    setValueInput(newName)
    setDataUpdate(
      {...dataUpdate, 
        name:newName,
        image:newImage
      }
    );   
    

    const formData = new FormData();
    formData.append("name",nameInput);
    formData.append("image",imageInput);
  
    console.log("-----> form data update")
    console.log(formData.get("name"))

    const url = "http://localhost:3000/crud/update/18";

      const fetchUploadHandler = async() => {
        try{
          const appel = await fetch(url, {
            method:"PUT",
            headers:{
              // "Content-Type": "application/json",
              "Authorization" : `Bearer ${dataContext.data.token}`
            },
            body:JSON.stringify(formData)
          });
          const response = await appel.json();
          console.log('----> response fetch')
          console.log(response);


          if(appel.ok){
            console.log("-----> reponse ok")
            console.log(response)
          }else{
            console.log("-----> reponse non ok")
            console.log(response)
          }

        }catch(err){
          console.log('On est dans le catch du PUT');
        }
      }
      
      fetchUploadHandler();
  }
  
  return (
    <section>
      <div>
        <hr />

        <div style={{
          margin:"0px 20px",
          display:"flex", 
          justifyContent:"center", 
          gap:"40px"
        }}>
          {
            isUpdate ?
            <div style={{display:"flex", flexDirection:"column"}}>
              <img 
                src={dataUpdate.image} 
                alt="" 
                style={{width:"200px", borderRadius:"20px"}} 
              />
              <input 
                type="file"
                ref={imageInput} 
                onChange={handleInput} 
                accept='.jpeg,.jpg,.png' 
                name="name_fiche_user"
              />
            </div>
            :
            <img 
              src={dataUpdate.image} 
              alt="" 
              style={{width:"200px", borderRadius:"20px"}} 
            />
          }


          <div style={{
            display:"flex", 
            flexDirection:"column", 
            alignItems:"center"
          }}>
            {
              isUpdate ? 
              <>
                <label htmlFor="nom">Nom</label>
                <input 
                  ref={nameInput} 
                  onChange={handleInput} 
                  type="text" 
                  value={dataUpdate.name} 
                  style={{color:"black"}} 
                  name="image_fiche_user"
                />
              </>
              :
              <>
                <h4>id : {dataUpdate.id}</h4>
                <h4>Nom : {dataUpdate.name}</h4>
              </>
            }
            <h5>Compte créé le : {dataUpdate.created_at}</h5>
            <br />
          </div>


        </div>
      </div>
      <div style={{display:"flex", gap:"20px", justifyContent:"center", marginTop:"20px"}}>
        <ButtonSubmit click={modifiyData} color={"warning"}>
          {!isUpdate ? "Modifier la fiche" : "Enregister les modifications"}
        </ButtonSubmit>
        <ButtonSubmit color={"danger"}>Supprimer la fiche</ButtonSubmit>
      </div>
    </section>
  )
}

export default FicheTest
